import React, {useState} from "react";
import SubjectCard from "./SubjectCard";
import ModalBox from "./ModalBox";




const Resources = ()=> {
  const subs = [{ id: 1, subName: "A", books: ["a", "b", "c"] }, { id: 2, subName: "B", books: ["b", "c"]},
                { id: 3, subName: "C", books: ["a", "b", "c"] }, { id: 4, subName: "D", books: ["b", "c"] },
                { id: 5, subName: "A", books: ["a", "b", "c"] }, { id: 6, subName: "B", books: ["b", "c"] },
                { id: 7, subName: "C", books: ["a", "b", "c"] }, { id: 8, subName: "D", books: ["b", "c"] }];

  const [modalContent, setModalContent] = useState(null);

  const showModal = (sub)=>{
    setModalContent(sub);
  }

  return(
    <div className="flex flex-wrap relative">
      {subs.map((sub)=>{
        return (<SubjectCard key={sub.id} data={sub} call={showModal} />);
      })}

      {console.log(modalContent)}
      {
        modalContent? <ModalBox modalContent={modalContent} call={showModal} />: null
      }
    </div>
  );

}

export default Resources;
